// CountIDs returns the count of ids and allows for filtering of the query post retrieval by IDs
func (hq *HushQuery) CountIDs(ctx context.Context) (int, error) {
	ctx = setContextOp(ctx, hq.ctx, ent.OpQueryIDs)
	if err := hq.prepareQuery(ctx); err != nil {
		return 0, err
	}

	qr := QuerierFunc(func(ctx context.Context, q Query) (Value, error) {
		return hq.IDs(ctx)
	})

	ids, err := withInterceptors[[]string](ctx, hq, qr, hq.inters)
	if err != nil {
		return 0, err
	}

	return len(ids), nil
}

// HushGroupBy is the group-by builder for Hush entities.
type HushGroupBy struct {
	selector
	build *HushQuery
}

// Aggregate adds the given aggregation functions to the group-by query.
func (_g *HushGroupBy) Aggregate(fns ...AggregateFunc) *HushGroupBy {
	_g.fns = append(_g.fns, fns...)
	return _g
}

// Scan applies the selector query and scans the result into the given value.
func (_g *HushGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, _g.build.ctx, ent.OpQueryGroupBy)
	if err := _g.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*HushQuery, *HushGroupBy](ctx, _g.build, _g, _g.build.inters, v)
}

func (_g *HushGroupBy) sqlScan(ctx context.Context, root *HushQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(_g.fns))
	for _, fn := range _g.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*_g.flds)+len(_g.fns))
		for _, f := range *_g.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*_g.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := _g.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

