// Scan applies the selector query and scans the result into the given value.
func (cds *CustomDomainSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, cds.ctx, ent.OpQuerySelect)
	if err := cds.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*CustomDomainQuery, *CustomDomainSelect](ctx, cds.CustomDomainQuery, cds, cds.inters, v)
}

func (cds *CustomDomainSelect) sqlScan(ctx context.Context, root *CustomDomainQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(cds.fns))
	for _, fn := range cds.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*cds.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := cds.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

