// CountIDs returns the count of ids with FGA batch filtering applied
func (nq *NarrativeQuery) CountIDs(ctx context.Context) (int, error) {
	logx.FromContext(ctx).Debug().Str("query_type", "Narrative").Str("operation", "count_ids").Msg("CountIDs: starting")

	ctx = setContextOp(ctx, nq.ctx, ent.OpQueryIDs)

	ids, err := nq.IDs(ctx)
	if err != nil {
		logx.FromContext(ctx).Error().Err(err).Str("query_type", "Narrative").Str("operation", "count_ids").Msg("CountIDs: IDs() failed")

		return 0, err
	}

	logx.FromContext(ctx).Debug().Str("query_type", "Narrative").Str("operation", "count_ids").Int("count", len(ids)).Msg("CountIDs: completed")

	return len(ids), nil
}

// NarrativeGroupBy is the group-by builder for Narrative entities.
type NarrativeGroupBy struct {
	selector
	build *NarrativeQuery
}

// Aggregate adds the given aggregation functions to the group-by query.
func (_g *NarrativeGroupBy) Aggregate(fns ...AggregateFunc) *NarrativeGroupBy {
	_g.fns = append(_g.fns, fns...)
	return _g
}

// Scan applies the selector query and scans the result into the given value.
func (_g *NarrativeGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, _g.build.ctx, ent.OpQueryGroupBy)
	if err := _g.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*NarrativeQuery, *NarrativeGroupBy](ctx, _g.build, _g, _g.build.inters, v)
}

func (_g *NarrativeGroupBy) sqlScan(ctx context.Context, root *NarrativeQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(_g.fns))
	for _, fn := range _g.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*_g.flds)+len(_g.fns))
		for _, f := range *_g.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*_g.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := _g.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

