// CountIDs returns the count of ids and allows for filtering of the query post retrieval by IDs
func (trq *TemplateRecipientQuery) CountIDs(ctx context.Context) (int, error) {
	ctx = setContextOp(ctx, trq.ctx, ent.OpQueryIDs)
	if err := trq.prepareQuery(ctx); err != nil {
		return 0, err
	}

	qr := QuerierFunc(func(ctx context.Context, q Query) (Value, error) {
		return trq.IDs(ctx)
	})

	ids, err := withInterceptors[[]string](ctx, trq, qr, trq.inters)
	if err != nil {
		return 0, err
	}

	return len(ids), nil
}

